import React from 'react';

// import data
import { workouts } from '../data';

//import swiper react components
import { Swiper, SwiperSlide } from 'swiper/react';

//import swiper styles
import 'swiper/css';
import 'swiper/css/navigation';
import '../workoutSlider.css';

//import required modules
import { Navigation } from 'swiper';

const WorkoutSlider = () => {
  //estrutura workout data
  const { programs } = workouts;

  return (
    <Swiper 
      slidesPerView={2} 
      spaceBetween={15} 
      navigation={true}
      breakpoints={{
        768: {
          slidesPerView: 3,
        },
        1024: {
          slidesPerView: 4,
        },
      }}
      modules={[Navigation]}
      className='workoutSlider'
    >
      {programs.map((program, idx) =>{
        //estrutura program
        const { image, name } = program;

        return (
          <SwiperSlide 
            className='max-w-[320px] max-h-[320px] relative' 
            key={idx}
          >
            <img 
              className='w-full h-full object-cover' 
              src={image} alt=''
            />
              <div className='absolute left-[20px] bottom-[20px] bg-white h-[26px] px-[14px] flex items-center rounded-[1px]'>
                <div
                  className='font-primary font-semibold text-sm text-neutral-500'
                >{name}</div>
              </div>
          </SwiperSlide>
        );
      })}
    </Swiper>
  );
};

export default WorkoutSlider;
